mapper.update:
update tbl_board set
	title = #{title},
	content = #{content, jdbcType=VARCHAR},
	writer = #{writer}
where bno = #{bno}


	update tbl_board set
		re_seq = re_seq + 1
	where re_group = #{re_group}
	and re_seq > #{re_seq}

update tbl_board set
	viewcnt = viewcnt + 1
where bno = #{bno}
